export EXPERIMENT=experiments/2025-03-31_21-06-47; find $EXPERIMENT -type f | grep return.json\$ | sort | xargs -P$(nproc) -I{} bash -c 'echo {},$(jq ".[-1].returns_mean" {})' | p2s.sort 'float(x.split(",")[-1])' | p2s.map 'float(x.split(",")[-1])' | p2s.hist --bins 20 --xlabel "Return" --title "1000 Sampled Drones => 1000 Trained Policies" --output 1000_policies_return.pdf && find $EXPERIMENT -type f | grep return.json\$ | sort | xargs -P$(nproc) -I{} bash -c 'echo {},$(jq ".[-1].episode_length_mean" {})' | p2s.sort 'float(x.split(",")[-1])' | p2s.map 'float(x.split(",")[-1])' | p2s.hist --bins 20 --xlabel "Episode Length" --title "1000 Sampled Drones => 1000 Trained Policies" --output 1000_policies_episode_length.pdf
